import styled from 'styled-components';

export const ButtonContainer = styled.button`
    border: 1px solid #FAFAFA;
    background-color: #FAFAFA;
    border-radius: 20px;

    height: 62px;
    width: 80%;

    margin: 20px 0;

    &: hover {
        background-color: #FAFAFA40;
        cursor: pointer;
    }
`
